import { useState } from 'react';
import '../styles/App.scss';
import Header from './header/Header';


function App() {

 const title= 'Componentes y props';
 const classHeader = 'header';
 const [listComic, setListComic] = useState([]);


 //useEffects
useEffect(() => {
fetch ('https://gateway.marvel.com/v1/public/comics?ts=1000&apikey=b819d866c81b5bd4e0cc05f9a943dba5&hash=d2920bc5270a5ef5760c5a3b1ce3e506')
.then(response=> response.json())
.then((dataApi) => {
  console.log(dataApi);
  setListComic(dataApi.data.results);
});
}, []);



  return (<div>
    {/* //los atributos de un componente son nombrados como queramos */}
    <Header title={title} subTitle="vamos con todo" cassCss={classHeader} />
  </div>
  );
}

export default App;

