import {useEffect, useState } from 'react';
import '../styles/App.scss';
import Header from './header/Header';
import ComicList from './ComicLists';
import obj from '../components/fetch';


function App() {

 const title= 'Componentes y props';
 const classHeader = 'header';
 const [listComic, setListComic] = useState([]);


 //useEffects
useEffect(() => {
obj.getDataApi().then((dataApi) => {
  console.log(dataApi);
  setListComic(dataApi.data.results);
});
}, []);





  return (<div>
    {/* //los atributos de un componente son nombrados como queramos */}
    <Header title={title} subTitle="vamos con todo" cassCss={classHeader} />
    <ComicList list={listComic}  />  {/* //{listComic} = variable de estado que le manda la Api */}
  </div>
  );
}

export default App;

